package principal;

public class Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//mais utilizada quando ja sabemos a quantidade de vezes que precisamos repetir os comandos 
		
		/*sintaxe do for
		 * for(variavel) que inicia o contador;
				condição que deve ser aceita;
				depois tem o incremento */
		
		for(int contador = 0; contador < 10; contador++) {
			System.out.println("Valor de contador: " + contador);
		}
		
		

	}

}
